package lecture_classes.day26_arrays_part3;

public class StringMan_Arrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String[] myCars = { "honda","mercedes"};
		
		System.out.println(myCars[0].length());
		System.out.println(myCars[1].charAt(2));
		System.out.println(myCars[0] == myCars[1]);
		
		//all of methods can be usable.
		
		
		

	}

}
